mapper.update:
update cuentas
set  cuenta = #{cuenta},
codigo = #{codigo},       
id_estado = #{id_estado},       
tipo_cuenta=#{tipo_cuenta} 
where  id_cuenta = #{id_cuenta}

update transacciones
set  transaccion = #{transaccion},
id_estado = #{id_estado},
fec_registro = #{fec_registro} 
where  id_transaccion = #{id_transaccion}
