mapper.update:
update dpr_provincias
set  provincia = #{provincia},
id_pais = #{id_pais},       
id_departamento = #{id_departamento},       
id_estado = #{id_estado}       
where  id_provincia = #{id_provincia}

mapper.select:
select id_provincia,provincia 
from dpr_provincias 
where id_pais= #{id_pais} 
and id_departamento=#{id_departamento}
